import{_ as t,c as d,o as a,V as r}from"./chunks/framework.OTU5OxHT.js";const x=JSON.parse('{"title":"退款订单","description":"","frontmatter":{},"headers":[],"relativePath":"daxpay/admin/order/退款订单.md","filePath":"daxpay/admin/order/退款订单.md","lastUpdated":1708092241000}'),e={name:"daxpay/admin/order/退款订单.md"},h=r('<h1 id="退款订单" tabindex="-1">退款订单 <a class="header-anchor" href="#退款订单">¶</a></h1><h2 id="功能介绍" tabindex="-1">功能介绍 <a class="header-anchor" href="#功能介绍">¶</a></h2><blockquote><p>退款订单是用户发起退款后，由系统生成的订单，用于管理退款相关信息。一个支付订单可能有多个退款订单，所以是一对多的关系。</p></blockquote><h3 id="退款订单-1" tabindex="-1">退款订单 <a class="header-anchor" href="#退款订单-1">¶</a></h3><table><thead><tr><th>参数</th><th>功能</th></tr></thead><tbody><tr><td>退款ID</td><td>订单的唯一标识</td></tr><tr><td>退款号</td><td>用户发起退款时传入的业务编号，不传输将于退款ID一致</td></tr><tr><td>原支付ID</td><td>原支付订单的支付ID</td></tr><tr><td>原业务号</td><td>原支付订单的业务号</td></tr><tr><td>网关订单号</td><td>如果退款中包含异步支付，三方支付网关中生成的订单号，不一定所有的通道都有，例如支付宝的退款订单就没有</td></tr><tr><td>原支付标题</td><td>原支付订单的标题</td></tr><tr><td>退款金额</td><td>这次退款的金额，单位为分</td></tr><tr><td>剩余可退款金额</td><td>这次退款之后，订单剩余的可退款金额，单位为分</td></tr><tr><td>订单金额信息</td><td>原支付订单的金额信息</td></tr><tr><td>退款信息</td><td>每个通道分别退款的金额信息</td></tr><tr><td>退款时间</td><td>退款完成的时间，如果有异步支付，读取网关中的完成时间</td></tr><tr><td>退款状态</td><td>退款订单的状态，见<a href="/daxpay/guides/other/常量和状态表.html#退款状态-refundstatusenum">退款状态</a></td></tr><tr><td>退款终端ip</td><td>发起退款的终端ip</td></tr><tr><td>错误码</td><td>出现错误时的状态码，目前是直接返回三方支付网关的错误码，供参考</td></tr><tr><td>错误信息</td><td>出现错误时的错误信息，</td></tr></tbody></table><h3 id="通道退款订单" tabindex="-1">通道退款订单 <a class="header-anchor" href="#通道退款订单">¶</a></h3><blockquote><p>如果对组合支付的订单进行退款时，会有多个退款订单，每个退款订单对应一个通道的退款订单，简单支付时，只会有一条通道的退款订单。</p></blockquote><table><thead><tr><th>参数</th><th>功能</th></tr></thead><tbody><tr><td>通道订单ID</td><td>订单标识</td></tr><tr><td>订单金额</td><td>原支付订单的金额</td></tr><tr><td>退款金额</td><td>退款的金额</td></tr><tr><td>通道支付单ID</td><td>进行退款支付通道订单的订单ID</td></tr><tr><td>剩余可退余额</td><td>这次退款之后，剩余的可退款金额，单位为分</td></tr><tr><td>退款通道</td><td>进行退款的通道，见<a href="/daxpay/guides/other/常量和状态表.html#支付通道-paychannelenum">支付通道</a></td></tr><tr><td>状态</td><td>订单的状态，见<a href="/daxpay/guides/other/常量和状态表.html#退款状态-refundstatusenum">退款状态</a></td></tr></tbody></table><h2 id="操作界面" tabindex="-1">操作界面 <a class="header-anchor" href="#操作界面">¶</a></h2><h3 id="退款订单列表" tabindex="-1">退款订单列表 <a class="header-anchor" href="#退款订单列表">¶</a></h3><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/bootx-img@master/daxpay/%E5%BE%AE%E4%BF%A1%E6%88%AA%E5%9B%BE_20240215020728.qzk78ukfttc.webp" alt="微信截图_20240215020728" loading="lazy"></p><h3 id="退款订单详情" tabindex="-1">退款订单详情 <a class="header-anchor" href="#退款订单详情">¶</a></h3><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/bootx-img@master/daxpay/%E5%BE%AE%E4%BF%A1%E6%88%AA%E5%9B%BE_20240215020757.3l3m6p4jaiu0.webp" alt="微信截图_20240215020757" loading="lazy"></p><h3 id="通道退款订单-1" tabindex="-1">通道退款订单 <a class="header-anchor" href="#通道退款订单-1">¶</a></h3><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/bootx-img@master/daxpay/%E5%BE%AE%E4%BF%A1%E6%88%AA%E5%9B%BE_20240215020742.62hf48k5c380.webp" alt="微信截图_20240215020742" loading="lazy"></p>',15),o=[h];function s(n,i,c,l,p,_){return a(),d("div",null,o)}const b=t(e,[["render",s]]);export{x as __pageData,b as default};
