import{_ as t,c as d,o as r,V as a}from"./chunks/framework.OTU5OxHT.js";const m=JSON.parse('{"title":"退款订单通知","description":"","frontmatter":{},"headers":[],"relativePath":"daxpay/gateway/notice/退款订单通知.md","filePath":"daxpay/gateway/notice/退款订单通知.md","lastUpdated":1708614643000}'),e={name:"daxpay/gateway/notice/退款订单通知.md"},h=a('<h1 id="退款订单通知" tabindex="-1">退款订单通知 <a class="header-anchor" href="#退款订单通知">¶</a></h1><h2 id="通知时机" tabindex="-1">通知时机 <a class="header-anchor" href="#通知时机">¶</a></h2><blockquote><p>当支付订单状态发生变化时，系统会主动将支付订单状态变化通知到商户的接收地址上。<strong>注意：调用接口发起退款时，出现的支付退款失败也不会触发通知，因为当前订单状态不会发生变化，依然是退款中</strong></p></blockquote><table><thead><tr><th>支付状态</th><th>描述</th><th>是否通知</th></tr></thead><tbody><tr><td>退款中</td><td>异步通道发起退款后，三方支付网关未返回结果时就是退款中，不会推送通知</td><td>否</td></tr><tr><td>退款成功</td><td>在存在异步通道情况下，退款成功后会推送通知</td><td>是</td></tr><tr><td>退款失败</td><td>在存在异步通道情况下，三方网关回调通知支付失败，会推送通知</td><td>是</td></tr></tbody></table><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项">¶</a></h2><ol><li>回调地址必须可被支付网关访问</li><li>回调地址仅支持http与https协议</li><li>如果文档开启验签，参照<a href="./../overview/签名规则.html">签名规则</a>文档进行验签</li></ol><h2 id="通知方式" tabindex="-1">通知方式 <a class="header-anchor" href="#通知方式">¶</a></h2><table><thead><tr><th>参数</th><th>说明</th></tr></thead><tbody><tr><td>请求方式</td><td><code>POST</code></td></tr><tr><td>请求类型</td><td><code>application/json</code></td></tr><tr><td>应答值</td><td>大写 <code>SUCCESS</code> 字符串</td></tr></tbody></table><h2 id="通知规则" tabindex="-1">通知规则 <a class="header-anchor" href="#通知规则">¶</a></h2><p>退款成功或失败时，系统会将支付订单状态变化通知到商户的接收地址上。商户需要接收处理该消息并返回应答。</p><p>如果收到客户系统的应答不符合规范或超时，支付网关会认为通知失败，并通过一定的策略定期重新发起通知，尽可能提高通知的成功率，但微信不保证通知最终能成功。 （通知频率为15s/15s/30s/3m/10m/20m/30m/30m/30m/60m/3h/3h/3h/6h/6h - 总计 24h4m）</p><h2 id="通知参数" tabindex="-1">通知参数 <a class="header-anchor" href="#通知参数">¶</a></h2><table><thead><tr><th>字段名<img width="70/"></th><th>变量名</th><th>必传</th><th>类型</th><th>示例值</th><th>描述</th></tr></thead><tbody><tr><td>支付ID</td><td>paymentId</td><td>是</td><td>Long</td><td>1753370980523384832</td><td>这次支付单的唯一标示</td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td></td><td></td><td></td><td></td><td></td><td></td></tr></tbody></table>',13),o=[h];function c(s,i,n,l,_,p){return r(),d("div",null,o)}const f=t(e,[["render",c]]);export{m as __pageData,f as default};
