import{_ as t,c as e,o as a,V as r}from"./chunks/framework.cllxpRy7.js";const m=JSON.parse('{"title":"认证终端管理","description":"","frontmatter":{},"headers":[],"relativePath":"platform/server/service/iam/认证终端管理.md","filePath":"platform/server/service/iam/认证终端管理.md","lastUpdated":1692957429000}'),d={name:"platform/server/service/iam/认证终端管理.md"},h=r('<h1 id="认证终端管理" tabindex="-1">认证终端管理 <a class="header-anchor" href="#认证终端管理">¶</a></h1><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述">¶</a></h2><p>该功能主要用来管理和配置不同类型的认证终端，包括：后台管理端、小程序端、可视化大屏端，以及做二次开发后，新的终端类型，通常可以把一个前端项目作为终端。 一个用户可以关联1-N个终端，只有关联了终端的账号才能登录。</p><h2 id="界面" tabindex="-1">界面 <a class="header-anchor" href="#界面">¶</a></h2><h2 id="功能说明" tabindex="-1">功能说明 <a class="header-anchor" href="#功能说明">¶</a></h2><ul><li>用于区分不同的的前端项目，方便进行对应的管理</li><li>不同的终端可以配关联的菜单，用于在不同的终端上展示不同的菜单</li><li>可以设置对应的安全配置，包括：最大密码错误数、修改密码是否需要重新登录、是否启用验证码、是否允许同时登录等</li></ul><h2 id="配置项" tabindex="-1">配置项 <a class="header-anchor" href="#配置项">¶</a></h2><table><thead><tr><th>名称</th><th>备注</th></tr></thead><tbody><tr><td>编码</td><td>唯一标识，用来区分不同的认证终端</td></tr><tr><td>名称</td><td>终端的名称</td></tr><tr><td>是否启用</td><td>该终端是否启用</td></tr><tr><td>系统内置</td><td>是否属于内置终端，内置终端无法删除</td></tr><tr><td>默认赋权</td><td>新注册的用户是否默认赋予该终端的权限</td></tr></tbody></table><h2 id="其他" tabindex="-1">其他 <a class="header-anchor" href="#其他">¶</a></h2>',9),s=[h];function i(c,o,_,n,l,f){return a(),e("div",null,s)}const b=t(d,[["render",i]]);export{m as __pageData,b as default};
